import logo from './logo.svg';
import './App.css';
import { greenBright } from 'colorette';

function App() {
  // Define the DOM tree as an object
const domTree = {
  name: 'A',
  children: [
    {
      name: 'B',
      children: [
        { name: 'E', children: [] },
        { name: 'F', children: [] }
      ]
    },
    {
      name: 'C',
      children: []
    },
    {
      name: 'D',
      children: [
        { name: 'G', children: [] },
        { name: 'H', children: [] }
      ]
    }
  ]
};

const domTree1 = {
  name: 'grig',
  children: [
    { 
    name: 'art',
     children:[
       {name:'asm',children:[]},
       {name:'dav', children: []}
     ]
    },
    {
      name: 'ars',
      children: 
      [
        {name:'raf', childern: [{name: 'mane', children:[]}]},
        {name: 'lil', children: []}
      ]
    },
    {
    name: 'arm',
    children: [
      {name: 'iri', children: []},
      {name: 'mik', children: []}
    ]

  }
]
};

// Function to traverse the DOM tree and find children of a given node
function findChildren(nodeName, tree) {
  // If the current node matches the target node, return its children
  if (tree.name === nodeName) {
    return tree.children.map(child => child.name);
  }

  // Recursively search for the target node in the children of the current node
  for (const child of tree.children) {
    const result = findChildren(nodeName, child);
    if (result.length > 0) {
      return result;
    }
  }

  // If the target node is not found, return an empty array
  return [];
}

// Example usage
const childrenOfB = findChildren('ars', domTree1);
console.log(childrenOfB); // Output: ['E', 'F']

  return (
    <div className="App">
      <header className="App-header">

          Learn React

      </header>
    </div>
  );
}

export default App;
